import "../styles/App.css";
import { useState, useEffect } from "react";
import { GetDataFromApi } from "./service/GetDataFromApi";
import { GetIcon } from "./GetIcon";
import { Filter } from "./Filter";

function App() {
  const [data, setData] = useState([]);
  const [species, setSpecies] = useState();
  useEffect(() => {
    GetDataFromApi().then((peopleArray) => {
      setData(peopleArray);
    });
  }, []);

  return (
    <>
      <Filter
        data={data}
        species={species}
        onChangeSpecies={(e) => setSpecies(e.currentTarget.value)}
      />
      <ul>
        {data
          .filter((card) => {
            if (species) {
              return true;
            } else {
              return species.includes(card.species);
            }
          })
          .map((card) => (
            <li key={card.id}>
              <img src={card.image} alt={card.name} />
              {card.name} <GetIcon species={card.species} />
              {card.status}
            </li>
          ))}
      </ul>
    </>
  );
}

export default App;
