// import "../styles/App.scss";
import { useState, useEffect } from "react";
import {
  GetDataFromApi,
  GetDataFromApibyName,
} from "../service/GetDataFromApi";
import { CharacterCard } from "./CharacterCard";
import { Filter } from "./Filter";
import { Header } from "./Header";

function CharacterList(image, status, name) {
  const [data, setData] = useState([]);
  const [fail, setFail] = useState(false);
  const [valueName, SetValueName] = useState();
  const [species, setSpecies] = useState();
  useEffect(() => {
    GetDataFromApi().then((peopleArray) => {
      setData(peopleArray);
    });
  }, []); //sin dependecia
  useEffect(() => {
    if (valueName) {
      GetDataFromApibyName(valueName)
        .then((peopleArray) => {
          setData(peopleArray);
        })
        .catch(() => {
          setFail(true);
        });
    }
  }, [valueName]); //con dependencia del valor del input

  return (
    <>
      <Header />
      <main className="main">
        <Filter
          valueName={valueName}
          onChangeName={(e) => SetValueName(e.currentTarget.value)}
          data={data}
          species={species}
          onChangeSpecies={(e) => setSpecies(e.currentTarget.value)}
        />
        <CharacterCard
          data={data}
          name={name}
          image={image}
          status={status}
          species={species}
        />
      </main>
    </>
  );
}

export { CharacterList };
