import "../styles/App.css";
import { useState, useEffect } from "react";
import { GetDataFromApi } from "./service/GetDataFromApi";
import { GetIcon } from "./GetIcon";
import { Filter } from "./Filter";
import { Header } from "./Header";

function App() {
  const [data, setData] = useState([]);
  const [species, setSpecies] = useState();
  useEffect(() => {
    GetDataFromApi().then((peopleArray) => {
      setData(peopleArray);
    });
  }, []);

  return (
    <>
      <Header />
      <Filter
        data={data}
        species={species}
        onChangeSpecies={(e) => setSpecies(e.currentTarget.value)}
      />
      <ul>
        {data
          .filter((card) => {
            if (!species) {
              return true;
            } else {
              return species === card.species;
            }
          })
          .map(
            (
              card //Esto va primero commit
            ) => (
              <li key={card.id}>
                <img src={card.image} alt={card.name} />
                {card.name} <GetIcon species={card.species} />
                {card.status}
              </li>
            )
          )}
      </ul>
    </>
  );
}

export default App;
