const GetIcon = (props) => {
  if (props.species === "Human") {
    return <i className="far fa-user" title="Humano"></i>;
  } else {
    return <i className="fab fa-reddit-alien" title="Alien"></i>;
  }
};

const GetIconDead = (props) => {
  if (props.status === "Dead") {
    return <i class="far fa-dizzy" title="Muerto"></i>;
  } else if (props.status === "Alive") {
    return <i class="fab fa-creative-commons-sampling-plus" title="Vivo"></i>;
  } else {
    return (
      <i className="fa-solid fa-location-question" title="Desconocido"></i>
    );
  }
};

export { GetIcon, GetIconDead };
